import React, { useEffect, useRef, useState } from "react";
import './index.css'

interface TimerViewProps {
  h: number
  m: number
  s: number
}

type Props = {
  title?: String
  timer?: Number
}

const CouponBanner: React.FC<Props> = (props: Props) => {
  const { title, timer } = props

  const dowmTimerRef = useRef<NodeJS.Timeout>()
  const [timerView, setTimerView] = useState<TimerViewProps | null>(null)
  const [timers, setTimers] = useState(24 * 60 * 60 * 1000)

  useEffect(() => {
    if (timers && timers !== 0) {
      dowmTimerRef.current = setTimeout(() => {
        setTimers(timer => timer - 1)
        setTimerView({
          h: (timers / 60 / 60) % 24,
          m: (timers / 60) % 60,
          s: timers % 60
        })
      }, 1000);
    }
    return () => {
      clearTimeout(dowmTimerRef.current)
    }
  }, [timers])

  return (
    <div className="wrapContent">
      <div className="title">{title}</div>
      <div className="content">
        {timerView?.h}:{timerView?.m}:{timerView?.s}
      </div>
    </div>
  )
}

export default CouponBanner
